import React, { useState, useEffect } from 'react';
import './App.css';
import logo from './logo.svg';
import Confetti from 'react-dom-confetti';

function App() {
  const [score, setScore] = useState(0);
  const [countdown, setCountdown] = useState(null);
  const [numDigits, setNumDigits] = useState(5);
  const [digitSequence, setDigitSequence] = useState([]);
  const [showSubmit, setShowSubmit] = useState(false);
  const [userSequence, setUserSequence] = useState([]);
  const [alertMessage, setAlertMessage] = useState("");
  const [reversedSequence, setReversedSequence] = useState([]);
  const confettiConfig = {
    angle: 90,
    spread: 360,
    startVelocity: 20,
    elementCount: 200,
    dragFriction: 0.12,
    duration: 3000,
    stagger: 3,
    width: "10px",
    height: "10px",
    perspective: "500px",
    colors: ["#a864fd", "#29cdff", "#78ff44", "#ff718d", "#fdff6a"]
  };

  function generateSequence() {
    const sequence = Array.from({ length: numDigits }, () => Math.floor(Math.random() * 10));
    setDigitSequence(sequence);
    setReversedSequence([...sequence].reverse());
    setCountdown(10);
  }

  useEffect(() => {
    if (digitSequence.length > 0) {
      setCountdown(10);
      const timer = setTimeout(() => {
        setDigitSequence([]);
        setShowSubmit(true);
      }, 10000);
      const countdownTimer = setInterval(() => {
        setCountdown(prevCountdown => prevCountdown - 1);
      }, 1000);
      return () => {
        clearTimeout(timer);
        clearInterval(countdownTimer);
      }
    }
  }, [digitSequence]);

  function checkSequence() {
    if (userSequence.join(' ') !== reversedSequence.join(' ')) {
      setAlertMessage(`Incorrect, try again. Expected sequence was: ${reversedSequence.join(' ')}`);
    } else {
      setAlertMessage('Correct!');
      setScore(prevScore => prevScore + numDigits - 4);
    }
    setUserSequence([]);
    setShowSubmit(false);
  }

  function resetGame() {
    setNumDigits(5);
    setDigitSequence([]);
    setShowSubmit(false);
    setUserSequence([]);
    setAlertMessage("");
    setReversedSequence([]);
  }

  const getFontSize = (sequenceLength) => {
    if (sequenceLength <= 10) {
      return '30px';
    } else if (sequenceLength <= 20) {
      return '20px';
    } else {
      return '15px';
    }
  };

  return (
    <div className="App">
      <h1>Phonologic Memory Game</h1>
      <h2>Your Score: {score}</h2>
      <Confetti active={alertMessage === 'Correct!'} config={confettiConfig} />
      <p> I want the sequence to be :
      <select value={numDigits} onChange={e => setNumDigits(Number(e.target.value))}>
        {Array.from({ length: 16 }, (_, i) => i + 5).map(num => (
          <option key={num} value={num}>{num}</option>
        ))}
      </select>
       numbers long</p>
      <p>Click "Generate Sequence" to start. A random sequence of numbers will be shown. Memorize this sequence, as it will disappear after 10 seconds. You will then have to enter the sequence in reverse.</p>
      <button onClick={generateSequence}>Generate Sequence</button>
      <p>Time left: {countdown}</p>
      <div className="sequence-container">
        {digitSequence.map((digit, i) => (
          <div key={i} className="sequence-item" style={{fontSize: getFontSize(digitSequence.length)}}>{digit}</div>
        ))}
      </div>
      {showSubmit && (
        <>
          <div className="sequence-container">
            {userSequence.map((num, i) => (
              <div key={i} className="user-sequence-item" style={{fontSize: getFontSize(userSequence.length)}}>{num}</div>
            ))}
          </div>
          <div className="number-pad">
            {Array.from({ length: 10 }, (_, i) => i).map(num => (
              <div key={num} className="sequence-item" onClick={() => setUserSequence(prevSequence => [...prevSequence, num])}>{num}</div>
            ))}
          </div>
          <button onClick={checkSequence}>Submit</button>
        </>
      )}
      {alertMessage && (
        <>
          <p>{alertMessage}</p>
          <button onClick={resetGame}>Try again</button>
        </>
      )}
    </div>
  );
}

export default App;
