import React, { useState, useEffect } from 'react';
import './App.css';
import logo from './logo.svg';


function App() {
  const [numDigits, setNumDigits] = useState(5);
  const [digitSequence, setDigitSequence] = useState([]);
  const [showSubmit, setShowSubmit] = useState(false);
  const [userSequence, setUserSequence] = useState("");
  const [alertMessage, setAlertMessage] = useState("");
  const [reversedSequence, setReversedSequence] = useState(''); // Nouvel état pour la séquence inversée

  function generateSequence() {
    const sequence = Array.from({ length: numDigits }, () => Math.floor(Math.random() * 10));
    setDigitSequence(sequence);
  }

  useEffect(() => {
    if (digitSequence.length > 0) {
      setReversedSequence([...digitSequence].reverse().join('')); // Définit l'état de la séquence inversée avant le timeout
      const timer = setTimeout(() => {
        setDigitSequence([]);
        setShowSubmit(true);
      }, 10000);
      return () => clearTimeout(timer);
    }
  }, [digitSequence]);

  function checkSequence() {
    const userSequenceWithoutSpaces = userSequence.replace(/\s/g, '');
    setUserSequence("");
    setShowSubmit(false);
    if (userSequenceWithoutSpaces === reversedSequence) { // Comparaison avec reversedSequence au lieu de digitSequence
      setAlertMessage('Correct!');
    } else {
      setAlertMessage(`Incorrect, try again. Expected sequence was: ${reversedSequence}`);
    }
  }

  function resetGame() {
    setNumDigits(5);
    setDigitSequence([]);
    setShowSubmit(false);
    setUserSequence("");
    setAlertMessage("");
    setReversedSequence(""); // Réinitialise également reversedSequence
  }

  return (
    <div className="App">
      <h1>Memory Game</h1>
      <select value={numDigits} onChange={e => setNumDigits(Number(e.target.value))}>
        {Array.from({ length: 16 }, (_, i) => i + 5).map(num => (
          <option key={num} value={num}>{num}</option>
        ))}
      </select>
      <button onClick={generateSequence}>Generate Sequence</button>
      <p>{digitSequence.join('')}</p>
      {showSubmit && (
        <>
          <input type="text" value={userSequence} onChange={e => setUserSequence(e.target.value)} />
          <button onClick={checkSequence}>Submit</button>
        </>
      )}
      {alertMessage && (
        <>
          <p>{alertMessage}</p>
          <button onClick={resetGame}>Try again</button>
        </>
      )}
    </div>
  );
}

export default App;
